import { useEffect, useState } from "react";
import HomeMain from "../components/HomeMain"

const BASE_URL = "https://api.themoviedb.org/3";
const TMDB_KEY = "14ce3cec0257cafce1c13e8ad25791ef";
const BASE_LANG = "ko";
const BASE_REGION = "KR";

function Home() {
  const [movies, setMovies] = useState([]);
  const getMovies = async () => {
    const response = await fetch(
      `${BASE_URL}/movie/now_playing?api_key=${TMDB_KEY}&language=${BASE_LANG}&region=${BASE_REGION}`
    );
    const json = await response.json();
    setMovies(json.results);
  }
  useEffect(() => {
    getMovies();
  }, [])
  useEffect(() => {
    console.log(movies)
    console.log(movies[1].backdrop_path)
  },);
  return (
    <div>
      <HomeMain mainMovie={movies[1].backdrop_path} />
      <div>
        <h1>Movies, Series List</h1>
      </div>
    </div>
  );
}

export default Home;
