import { json, useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import styles from "./MovieInfo.module.css"

const BASE_URL = "https://api.themoviedb.org/3";
const TMDB_KEY = "14ce3cec0257cafce1c13e8ad25791ef";
const BASE_LANG = "ko";
const BASE_REGION = "KR";

function MovieInfo() {
  const { id } = useParams();
  const [info, setInfo] = useState([]);
  const [credit, setCredit] = useState([]);
  const [loading, setLoading] = useState(true);

  const getInfo = async () => {
    const response = await fetch(
      `${BASE_URL}/movie/${id}?api_key=${TMDB_KEY}&language=${BASE_LANG}&region=${BASE_REGION}`
    );
    const json = await response.json();
    console.log(json);
    setInfo(json);
    setLoading(false);
  }

  const getCredit = async () => {
    const response = await fetch(
      `${BASE_URL}/movie/${id}/credits?api_key=${TMDB_KEY}&language=${BASE_LANG}&region=${BASE_REGION}`
    );
    const json = await response.json();
    console.log(json);
    setCredit(json);
  }

  const imageUrl = `https://image.tmdb.org/t/p/original${info.poster_path}`;

  useEffect(() => {
    getInfo();
  }, [])

  useEffect(() => {
    getCredit();
  }, [])


  return (
    <div>
      {loading ? (<h1>Loading...</h1>) : (
        <div className={styles.main}>
          <div className={styles.content}>

          </div>
          <div
            style={{
              background: `linear-gradient(to left, rgba(0, 0, 0, 0.733), rgba(0, 0, 0, 0.733)), url(${imageUrl})`,
              backgroundRepeat: "no-repeat",
              backgroundSize: "cover",
              backgroundPosition: "left"
            }}
            className={styles.background}
          >
          </div>
        </div>


      )}
    </div>
  )
}

export default MovieInfo;
