import styles from "./HomeMain.module.css"

function HomeMain({ main }) {
  // const addUrl = main.backdrop_path;
  // const imgUrl = "https://image.tmdb.org/t/p/w1280/" + addUrl;
  return (
    <div className={styles.main} style={{
      backgroundImage: `url(https://image.tmdb.org/t/p/w1280/)`,
    }}>
      <div className={styles.mainInfo}>
        <h1 className={styles.mainTitle}>{main.title}</h1>
        <button className={styles.mainBtn} id={styles.firsBtn}>재생</button>
        <button className={styles.mainBtn} id={styles.secBtn}>상세 정보</button>
      </div>
    </div>
  )
};

export default HomeMain;
