import { useEffect, useState } from "react";

import Slider from "react-slick";
import '../../node_modules/slick-carousel/slick/slick.css';
import '../../node_modules/slick-carousel/slick/slick-theme.css';

import styles from "./MoviesSlide.module.css";

const BASE_URL = "https://api.themoviedb.org/3";
const TMDB_KEY = "14ce3cec0257cafce1c13e8ad25791ef";
const BASE_LANG = "ko";
const BASE_REGION = "KR";

function MoviesSlide() {
  const [movies, setMovies] = useState([]);

  const getMovies = async () => {
    const response = await fetch(
      `${BASE_URL}/movie/now_playing?api_key=${TMDB_KEY}&language=${BASE_LANG}&region=${BASE_REGION}`
    );
    const json = await response.json();
    setMovies(json.results);
  }
  const settings = {
    arrow: false,
    dots: false,
    infinite: true,
    speed: 700,
    slidesToShow: 9,
    slidesToScroll: 8,
    responsive: [ // 반응형 웹 구현 옵션
      {
        breakpoint: 1500,
        settings: {
          slidesToShow: 8,
          slidesToScroll: 7,
        }
      },
      {
        breakpoint: 1215, // 화면 사이즈
        settings: {
          slidesToShow: 6,
          slidesToScroll: 5,
        }
      },
      {
        breakpoint: 950,
        settings: {
          slidesToShow: 5,
          slidesToScroll: 4,
        }
      },
      {
        breakpoint: 800,
        settings: {
          slidesToShow: 4,
          slidesToScroll: 4,
        }
      },
      {
        breakpoint: 630,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
        }
      }
    ]
  };

  useEffect(() => {
    getMovies();
  }, [])
  useEffect(() => {
    console.log(movies);
  },);

  return (
    <div>
      <Slider {...settings}>
        {movies.map((movie) => {
          const imageUrl = `https://image.tmdb.org/t/p/original${movie.poster_path}`;
          return (
            <div key={movie.id}>
              <img src={imageUrl} alt="moviePoster" className={styles.moviePoster} />
              <div className={styles.posterInfo}>
                <h2 className={styles.movieTitle}>{movie.title}</h2>
                <span className={styles.movieVote}>{movie.vote_average}</span>
              </div>
            </div>
          )
        })}
      </Slider>
    </div>

  )
};

export default MoviesSlide;
