import { useEffect, useState } from "react";
import HomeMain from "../components/HomeMain"
import MoviesSlide from "../components/MoviesSlide";

const BASE_URL = "https://api.themoviedb.org/3";
const TMDB_KEY = "14ce3cec0257cafce1c13e8ad25791ef";
const BASE_LANG = "ko";
const BASE_REGION = "KR";

function Home() {
  const [movies, setMovies] = useState([]);
  const getMovies = async () => {
    const response = await fetch(
      `${BASE_URL}/movie/now_playing?api_key=${TMDB_KEY}&language=${BASE_LANG}&region=${BASE_REGION}`
    );
    const json = await response.json();
    setMovies(json.results);
  }
  useEffect(() => {
    getMovies();
  }, [])
  useEffect(() => {
    console.log(movies);
  },);
  return (
    <div>
      <HomeMain main={movies[1]} />
      <div>
        {movies.map((movie) => {
          const imageUrl = `https://image.tmdb.org/t/p/original${movie.poster_path}`;
          return (
            <MoviesSlide
              key={movie.id}
              imageUrl={imageUrl}
              title={movie.title}
              vote={movie.vote_average}
            />
          )
        })}
      </div>
    </div>
  );
}

export default Home;
