import { useEffect, useState } from "react";

function Home() {
  const [movies, setMovies] = useState([]);
  const getMovies = async () => {
    const response = await fetch(
      "https://api.themoviedb.org/3/movie/now_playing?api_key=14ce3cec0257cafce1c13e8ad25791ef"
    );
    const json = await response.json();
    setMovies(json.results);
  }
  useEffect(() => {
    getMovies();
  }, [])
  return (
    <div>
      <h1>Movies</h1>
      {`https://www.themoviedb.org/t/p/w1920_and_h800_multi_faces/${movies.poster_path}`}
    </div>
  );
}

export default Home;
