import styles from "./HomeMain.module.css"

function HomeMain({ main }) {
  // const addUrl = main.backdrop_path;
  // const imgUrl = "https://image.tmdb.org/t/p/w1280/" + addUrl;
  return (
    <div className={styles.main} style={{
      backgroundImage: `url(https://image.tmdb.org/t/p/w1280/rLb2cwF3Pazuxaj0sRXQ037tGI1.jpg)`,
    }}>
      <div className={styles.mainInfo}>
        <h1 className={styles.mainTitle}>오펜하이머</h1>
        <button className={styles.mainBtn} id={styles.firstBtn}><span class="material-symbols-outlined" id={styles.firstIcon}>
          play_arrow
        </span>재생</button>
        <button className={styles.mainBtn} id={styles.secBtn}><span class="material-symbols-outlined" id={styles.secIcon}>
          info
        </span>상세 정보</button>
      </div>
    </div>
  )
};

export default HomeMain;
